    private void btnCalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcActionPerformed
        // TODO add your handling code here:
        float n1 = Float.parseFloat(text1.getText());
        float n2 = Float.parseFloat(text2.getText());
        float n3 = Float.parseFloat(text3.getText());
        float n4 = Float.parseFloat(text4.getText());
        
        float mN1 = (n1 + n2)/2;
        float mN2 = (n3 + n4)/2;
        
        float media = (2*mN1 + 3*mN2)/5;
        
        lblResult.setText(Float.toString(media));
        
    }//GEN-LAST:event_btnCalcActionPerformed

